import { ErrorMessage, Field, Form, Formik } from 'formik'
import { FilePicker } from 'evergreen-ui'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { fetchCategories, fetchSupplier } from '../../redux/features/slices/SliceOne';
import { useNavigate } from 'react-router-dom';
import { productInitialValue, productValidationSchema } from '../../form_validations';
import axios from 'axios';
import { toast } from 'react-toastify';
import JoditEditor from 'jodit-react';

const Product = () => {
  const [click, setClick] = useState(false);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const category = useSelector(state => state.data.data);
  const supplier = useSelector(state => state.data.supplierList.data);
  console.log('====================================');
  console.log(supplier);
  console.log('====================================');
  useEffect(() => {
    dispatch(fetchCategories());
    dispatch(fetchSupplier());
  }, [])
  return (
    <Formik
      initialValues={productInitialValue}
      validationSchema={productValidationSchema}
      onSubmit={async (values, { setSubmitting, resetForm }) => {
        // values.status = isChecked;
        try {
          const response = await axios.post("http://localhost:8000/api/v1/add_supplier", values,
            {
              headers: {
                Accept: "application/json",
                Authorization: `Bearer ${sessionStorage.getItem("token")}`,
              }
            }
          );
          setClick(true);
          if (response.data.status === "success") {
            toast.success(response.data.message);
            setClick(false);
            setSubmitting(false);
            resetForm();
          }
        } catch (error) {
          toast.error(error.response.data.message);
          setClick(false);
          setSubmitting(false);
        }
      }}
    >
      {({ values, setFieldValue }) => (
        <Form className="w-full max-w-3xl overfloy-scroll h-[40rem] py-5'">
          <div className="flex flex-wrap -mx-3 mb-6 ">
            <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
              <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-product-name">
                Product Name
              </label>
              <Field className="appearance-none block w-full bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-product-name" type="text" placeholder="Name" name="name" />
              <p className="text-red-500 text-xs italic">Please fill out this field.</p>
            </div>
            <div className="w-full md:w-1/2 px-3">
              <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-image-name">
                image
              </label>
              <FilePicker
                height={45}
                placeholder="Select your file"
              />
              {/* <Field className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-image-name" type="file" placeholder="Doe" /> */}
            </div>
          </div>
          <div className="flex flex-wrap -mx-3 mb-6">
            <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
              <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-category">
                Product Category
              </label>
              <Field as="select" className="appearance-none block w-full bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" name="product_category">
                {
                  category && category.map(
                    (category, index) => {
                      return (
                        <option key={index} value={category.id}>{category.category_name}</option>
                      );
                    }
                  )
                }
              </Field>
              <p className="text-red-500 text-xs italic">Please fill out this field.</p>
            </div>
            <div className="w-full md:w-1/2 px-3">
              <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-supplier-name">
                Supplier
              </label>
              <Field as="select" className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-supplier-name" name="supplier_id">
                {
                  supplier && supplier.map(
                    (supplier, index) => {
                      return (
                        <option key={index} value={supplier.id}>{supplier.name}</option>
                      );
                    }
                  )
                }
              </Field>
            </div>
          </div>
          <div className="flex flex-wrap -mx-3 mb-6">
            <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
              <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-first-name">
                MRP ($)
              </label>
              <Field className="appearance-none block w-full bg-gray-200 text-gray-700 border rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-first-name" type="text" placeholder="Jane" />
              <p className="text-red-500 text-xs italic">Please fill out this field.</p>
            </div>
            <div className="w-full md:w-1/2 px-3">
              <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-last-name">
                Discount ($)
              </label>
              <Field className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="text" placeholder="Doe" />
            </div>
          </div>
          {/* <div className="flex flex-wrap -mx-3 mb-6"> */}
            <div className="flex flex-wrap flex-col -mx-3 mb-20">
              <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-password">
                Description
              </label>
              <JoditEditor
                name="description"
                value={values.description} // Set value from Formik
                onChange={value => {
                  setFieldValue('description', value)
                }}
              />
              <p className="text-red-600 text-xs italic"><ErrorMessage name='description' /></p>
            </div>
          {/* </div> */}
          <div className='w-full flex justify-end py-5'>
            <button type="button" onClick={() => navigate("/home")} className="text-gray-500  focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm w-full sm:w-auto px-5 py-2.5 text-center">Cancel</button>
            {click ? (<button disabled type="button" className="py-2.5 px-5 me-2 text-sm font-medium text-gray-900 bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-4 focus:outline-none focus:ring-blue-700 focus:text-blue-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700 inline-flex items-center">
