import React, { useState } from 'react'
import { Formik, Field, Form, ErrorMessage } from 'formik';
import { staffInitialValue, staffValidationSchema } from '../../form_validations';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
const Staff = () => {
  const [click, setClick] = useState(false);
  const navigate = useNavigate();
  const [phone, setphone] = useState('');
  const [zipCode, setZipCode] = useState('');
  const [isChecked, setIsChecked] = useState(true); // Initial state for checkbox
  const handleToggle = () => {
    setIsChecked(!isChecked); // Toggle the checked state
  };
  const handlePhoneChange = (event) => {
    const input = event.target.value;
    // Filter out non-numeric characters
    const numericInput = input.replace(/\D/g, '');
    setphone(numericInput);
  };
  const handleZipCodeChange = (event) => {
    const input = event.target.value;
    // Filter out non-numeric characters
    const numericInput = input.replace(/\D/g, '');
    setZipCode(numericInput);
  };

  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState('Parent');

  const options = ['Parent', 'English', 'Arabic', 'Hindi', 'Spanish', 'French', 'Japanese', 'Korean'];

  const toggleOptionsList = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setIsOpen(false);
  };





  return (
    <>
      <Formik initialValues={staffInitialValue} validationSchema={staffValidationSchema}

        onSubmit={async (values, { setSubmitting, resetForm }) => {
          const credentials = {
            email: values.email,
            password: values.password
          }
          try {
            const response = await axios.post("http://localhost:8000/api/v1/login", credentials,
              {
                headers: {
                  Accept: "application/json"
                }
              }
            );

            setClick(true);
            if (response.data.status === "success") {
              toast.success(response.data.message);
              var token = response.data.data.token.token;
              sessionStorage.setItem("token", token);
              navigate("/home");
              setClick(false);
              setSubmitting(false);
              resetForm();
            }
          } catch (error) {
            toast.error(error.response.data.message);
            setClick(false);
            setSubmitting(false);
          }
        }}
      >
        <Form className="max-w-md mx-auto">
          <div className="grid md:grid-cols-2 md:gap-6">
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="first_name" id="floating_first_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="floating_first_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">First name <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="first_name" /></p>
            </div>
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="ast_name" id="floating_last_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="last_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Last name <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="last_name" /></p>
            </div>
          </div>
          <div className="grid md:grid-cols-2 md:gap-6">
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="Address_line_1" id="floating_first_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="floating_first_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Address Line 1 <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="Address_line_1" /></p>
            </div>
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="Address_line_2" id="floating_last_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="floating_last_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Address Line 2</label>
            </div>
          </div>
          <div className="grid md:grid-cols-2 md:gap-6">
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="country" id="floating_first_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="floating_first_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Country <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="country" /></p>
            </div>
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="state" id="floating_last_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="floating_last_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">State <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="state" /></p>
            </div>
          </div>
          <div className="grid md:grid-cols-2 md:gap-6">
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="city" id="floating_first_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="floating_first_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">City <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="city" /></p>
            </div>
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="zip_code" id="floating_last_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " value={zipCode} onInput={handleZipCodeChange} />
              <label htmlFor="floating_last_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Zip Code <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="zip_code" /></p>
            </div>
          </div>
          <div className="grid md:grid-cols-2 md:gap-6">
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="phone" id="floating_first_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " value={phone} onInput={handlePhoneChange} />
              <label htmlFor="floating_first_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Phone <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="phone" /></p>
            </div>
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="email" id="floating_last_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " />
              <label htmlFor="floating_last_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Email <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="email" /></p>
            </div>
          </div>
          <div className="grid md:grid-cols-2 md:gap-6">
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <Field type="text" name="password" id="floating_first_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-black dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder="" />
              <label htmlFor="floating_first_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Password <span className="text-red-500">*</span></label>
              <p className='text-red-500 absolute text-sm my-2'><ErrorMessage name="password" /></p>
            </div>
            <div className="relative z-0 w-full mb-5 group h-[80px]">
              <div className="select-menu mt-[15px]">
                <div className="select bg-white-800 text-black border-2 rounded flex justify-between items-center cursor-pointer" onClick={toggleOptionsList}>
                  <span>{selectedOption}</span>
                  <i className={`fas ${isOpen ? 'fa-angle-up' : 'fa-angle-down'}`}></i>
                </div>
                <div className={`options-list bg-white rounded mt-2 overflow-auto max-h-48 transition-all duration-300 ${isOpen ? 'opacity-100 scale-y-100' : 'opacity-0 scale-y-0'}`}>
                  {options.map((option, index) => (
                    <div key={index} className="option py-2 px-4 cursor-pointer hover:text-blue-500" onClick={() => handleOptionClick(option)}>{option}</div>
                  ))}
                </div>
