import { ErrorMessage, Field, Form, Formik } from 'formik'
import { FilePicker } from 'evergreen-ui'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { fetchCategories, fetchSupplier } from '../../redux/features/slices/SliceOne';
import { useNavigate } from 'react-router-dom';
import { productInitialValue, productValidationSchema } from '../../form_validations';
import axios from 'axios';
import { toast } from 'react-toastify';
import JoditEditor from 'jodit-react';

const Product = () => {
  const [MRP, setMRP] = useState('')
  const [discount, setDiscount] = useState('')
  const [click, setClick] = useState(false);
  const navigate = useNavigate();
  const [isChecked, setIsChecked] = useState(true);
  const dispatch = useDispatch();
  const category = useSelector(state => state.data.data);
  const supplier = useSelector(state => state.data.supplierList.data);
  const handleToggle = () => {
    setIsChecked(!isChecked); // Toggle the checked state

  };
  useEffect(() => {
    dispatch(fetchCategories());
    dispatch(fetchSupplier());
  }, [])
  const handleMRP = (event) => {
    let input = event.target.value;
    // Limit the input to 10 digits
    input = input.slice(0, 6);

    // Filter out non-numeric characters
    const numericInput = input.replace(/\D/g, '');
    setMRP(numericInput);
  };
  const handleDiscount = (event) => {
    let input = event.target.value;
    input = input.slice(0, 2);
    // Filter out non-numeric characters
    const numericInput = input.replace(/\D/g, '');
    setDiscount(numericInput);
  };
  return (

    <Formik
      initialValues={productInitialValue}
      validationSchema={productValidationSchema}
      onSubmit={async (values, { setSubmitting, resetForm }) => {
        values.status = isChecked;
        try {
          const response = await axios.post("http://localhost:8000/api/v1/add_supplier", values,
            {
              headers: {
                Accept: "application/json",
                Authorization: `Bearer ${sessionStorage.getItem("token")}`,
              }
            }
          );
          setClick(true);
          if (response.data.status === "success") {
            toast.success(response.data.message);
            setClick(false);
            setSubmitting(false);
            resetForm();
          }
        } catch (error) {
          toast.error(error.response.data.message);
          setClick(false);
          setSubmitting(false);
        }
      }}
    >

    </Formik>
  )
}

export default Product
