            <div className='w-full bg-gray-50 dark:bg-gray-900'>

                <SideBar />
                <div className='flex flex-col w-4/5 bg-slate-100'>
                    <Header />
                    <section className='flex flex-1 h-full overflow-y-scroll'>
                        <Outlet />
                    </section>
                </div>
            </div>
        </>
    )
}

export default RootLayout
